import React, {useState, useEffect} from 'react'
import CartItem from './CartItem.js'

import GooglePayButton from '@google-pay/button-react'

function Cart() {

    let [cartItems, setCartItems] = useState([])
    let [grandTotal, setGrandTotal] = useState(0.00)
    let pricee = 0

    useEffect(()  => {
    grabCart()
}, [])

    function grabCart() {
        fetch('/getcart/')
        .then(res => res.json())
        .then((data) => {
            makeItems(data)
        })
    }

    let gpButton = (

        <GooglePayButton
        className="centered"
            environment="TEST"			//TEST or PRODUCTION
            paymentRequest={{
                apiVersion:2,
                apiVersionMinor:0,
                allowedPaymentMethods:[
                    {
                        type:"CARD",
                        parameters: {
                            allowedAuthMethods:["PAN_ONLY", "CRYPTOGRAM_3DS"],
                            allowedCardNetworks:["MASTERCARD", "VISA"]
                        },

                        tokenizationSpecification:{
                            type:"PAYMENT_GATEWAY",
                            parameters:{
                                gateway:"example",
                                GatewayMerchantId:"example"
                            }
                        }
                    }
                ],

                            merchantInfo:{
                                merchantId:"12345678",
                                merchantName:"Demo Merchant"
                            },

                            transactionInfo:{
                                totalPriceStatus:"FINAL",
                                totalPriceLabel:"Total",
                                totalPrice:price.toString(),
                                currencyCode:"USD",
                                countryCode:"US"
                            },
                            shippingAddressRequired:true,
                            callbackIntents:["PAYMENT_AUTHORIZATION"]
                        }}

                        onLoadPaymentData={paymentRequest => paymentRequest }
                        onPaymentAuthorized={paymentData => {
                            console.log("a", paymentData)
                            return { transactionState: "SUCCESS" }
                        }}
                        existingPaymentMethodRequired="false"
                        buttonColor="Black"
                        buttonType="Buy"

            >
                </GooglePayButton>
            )


            function bigTotal(price) {
                console.log("grandTotal")
                setGrandTotal(grandTotal + price)
            }


    function makeItems(cartData) {

        if (cartData) { 
           setCartItems (Object.keys(cartData).map(i => <CartItem bigTotal={bigTotal} item={i} amount={cartData[i]} redoItems={redoItems}  key={i} />) )
        }
    }

    function redoItems() {
        setCartItems([])
        grabCart()
    }



    return(
        <div>
            <h1 className='middleText'>Cart</h1>
            {cartItems}
            <br />
            Grand Total{grandTotal}
            <div>{gpButton}</div>


        </div>
    )
}

export default Cart
