import React, {useState, useEffect} from 'react'
import CartItem from './CartItem.js'

function Cart() {

    let [cartItems, setCartItems] = useState([])

    useEffect(()  => {
    grabCart()
}, [])

    function grabCart() {
        fetch('/getcart/')
        .then(res => res.json())
        .then((data) => {
            makeItems(data)
        })
    }

    let gpButton = (

<GooglePayButton
	environment="TEST"			//TEST or PRODUCTION
	paymentRequest={{
		apiVersion:2,
		apiVersionMinor:0,
		allowedPaymentMethods:[
			{
				type:"CARD",
				parameters: {
					allowedAuthMethods:["PAN_ONLY", "CRYPTOGRAM_3DS"],
					allowedCardNetworks:["MASTERCARD", "VISA"]
				},

				tokenizationSpecification:{
					type:"PAYMENT_GATEWAY",
					parameters:{
						gateway:"example",
						GatewayMerchantId:"example"
					}
                }
            }
        ],

					merchantInfo:{
						merchantId:"12345678",
						merchantName:"Demo Merchant"
					},

					transactionInfo:{
						totalPriceStatus:"FINAL",
						totalPriceLabel:"Total",
						totalPrice:price.toString(),
						currencyCode:"USD",
						countryCode:"US"
					},
					shippingAddressRequired:true,
					callbackIntents:["PAYMENT_AUTHORIZATION"]
				}}

				onLoadPaymentData={paymentRequest => paymentRequest }
				onPaymentAuthorized={paymentData => {
					console.log("a", paymentData)
					return { transactionState: "SUCCESS" }
				}}
				existingPaymentMethodRequired="false"
				buttonColor="Black"
				buttonType="Buy"

	>
		</GooglePayButton>
    )


    function makeItems(cartData) {

        if (cartData) { 
           setCartItems (Object.keys(cartData).map(i => <CartItem item={i} amount={cartData[i]} redoItems={redoItems}  key={i} />) )
        }
    }

    function redoItems() {
        setCartItems([])
        grabCart()
    }



    return(
        <div>
            <h1 className='middleText'>Cart</h1>
            {cartItems}
            <br />
            <button className='centered'>Check Out</button>


        </div>
    )
}

export default Cart
