function DeleteAcct() {

    useEffect(() => {
       if (!message) { youGetMe() }
    }, [])

    let [message, setMessage] = useState(null)

    function youGetMe() {

        fetch("/getme/")
        .then(res => res.json())
        .then(data => { console.log(data)
            if(data.id) {deleteUser("/users/" + data.id)}
        })
      }

    function deleteUser(url) {
        fetch(url, {method: 'DELETE'})
        .then(res => res.json())
        .then(data => {
            setMessage("Your account has been deleted.")
        })


    }



    return(
        <div>
            (<p>{message}</p>)
        </div>
    )
}

export default DeleteAcct
