    let [itemData, setItemData] = useState({})


    useEffect( () => {
        grabItem()
    }, [])

    function grabItem() {
        let url = '/items/' + item
        console.log(url)
        fetch(url)
        .then(res => res.json())
        .then(data => setItemData(data))
    }

    return(
        <div>
        {itemData && ( <div className= 'CartItem'>  <p><img className="CartItemPic" src={itemData.picurl} /> </p>
        <div className='CartItemData'> {itemData.name}
        </div>

        </div>


        )}

        </div>
    )
}

